<?php// Process login / FB connect / New user account via FB connectfunction _process_login(){  $email=trim($_POST['email']);  $password=md5($_POST['password'].$GLOBALS['config']['secretstring']);  $rememberme=isset($_POST['rememberme']);    $user=new User();  if(!empty($email)){    $user->retrieve_one('email=?',$email);  }  if (!$email || !$password){    $error = true;    $_SESSION['login']['err_msg'] = 'Please Key In Email & Password!';        } if($user->exists()&& $password!=$user->get('password')){      $error = true;      unset($_SESSION['authuid']);      $_SESSION['login']['err_msg'] = 'Username or password is incorrect';       }    if(!$error){  $_SESSION['authuid']= $user->get('id');  // $user->login($rememberme);  redirect('user/profile');      }  else{    redirect('user/login');      }  }?>