<?phpClass Directory_Category extends Model{  function __construct($id='')  {		parent::__construct('Directory_Category_ID','T_Directory_Category','getdbh');		$this->rs['Directory_Category_ID']=0;		$this->rs['Name']='';		$this->rs['Tag']='';		$this->rs['Meta_Description']='';		$this->rs['Page_Title']='';		$this->rs['Display']='';		$this->rs['Sort']='';		$this->rs['Created_By_ID']='';		$this->rs['Created_Date']='';		$this->rs['Last_Updated_By_ID']='';		$this->rs['Last_Updated_Date']='';  if ($id)    $this->retrieve($id);  }    function retrieve_all_categories()  {    $dc = new Directory_Category();    $dc_arr = $dc->retrieve_many('display="y" ORDER BY sort ASC','');    return $dc_arr;  }}?>